import { css } from 'uebersicht'

/***********************Options***********************/
// sign up here: https://home.openweathermap.org/users/sign_up
const KEY = 'ENTER_YOUR_OPENWEATHERMAP_KEY_HERE'
// https://openweathermap.org/current#multi
const LANG = 'en' // language: en for English, zh for Chinese
// weather data units - can be metric, imperial, or default (Kelvin)
const UNITS = 'imperial'
// theme: light or dark
const theme = 'dark'
// background transparency. value from 0 to 1
const backgroundTransparency = 0.75
// widget position
const POSITION = {
  x: 'right', // x is left or right
  y: 'top', // y is top or bottom
  marginX: 20,
  marginY: 20,
}
// how likely do you think it will rain in your area...
const CONFIDENCE_OF_RAINFALL = 0.5
/***********************Options***********************/

let fontColor
let fontSubColor
let backgroundColor
switch (theme) {
  case 'dark':
    fontColor = '#fff'
    fontSubColor = '#b2b2b2'
    backgroundColor = `rgba(0, 0, 0, ${backgroundTransparency})`
    break
  default:
    // default is light
    fontColor = 'rgba(0, 0, 0, 0.85)'
    fontSubColor = '#444'
    backgroundColor = `rgba(255, 255, 255, ${backgroundTransparency})`
    break
}

export const refreshFrequency = 30 * 60 * 1000 // ms, every 30 minutes

export const initialState = {
  loading: true,
  data: null,
  city: '',
  showMore: false,
}

const WEEK_DAY = {
  en: [
    'Sunday',
    'Monday',
    'Tuesday',
    'Wednesday',
    'Thursday',
    'Friday',
    'Saturday',
  ],
  zh: [
    '星期日',
    '星期一',
    '星期二',
    '星期三',
    '星期四',
    '星期五',
    '星期六',
  ],
}
const TEXT = {
  today: {
    en: 'Today',
    zh: '今天',
  },
  feelsLike: {
    en: 'Feels like: ',
    zh: '体感温度：',
  },
}
// https://openweathermap.org/weather-conditions#Weather-Condition-Codes-2
const ICON = {
  // clearsky
  '01d':
    'data:image/png;base64,',
  '01n':
    'data:image/png;base64,',
  //rain
  '10d':
    'data:image/png;base64,',
  '10n':
    'data:image/png;base64,',
  //snow
  '13d':
    'data:image/png;base64,',
  '13n':
    'data:image/png;base64,',
  // mist
  '50d':
    'data:image/png;base64,',
  '50n':
    'data:image/png;base64,',
  // few clouds
  '02d':
    'data:image/png;base64,',
  '02n':
    'data:image/png;base64,',
  // scattered clouds
  '03d':
    'data:image/png;base64,',
  '03n':
    'data:image/png;base64,',
  // broken clouds
  '04d':
    'data:image/png;base64,',
  '04n':
    'data:image/png;base64,',
  // shower rain - like light rain
  '09d':
    'data:image/png;base64,',
  '09n':
    'data:image/png;base64,',
  '11d':
    'data:image/png;base64,',
  '11n':
    'data:image/png;base64,',
}

export const command = (dispatch) => {
  geolocation.getCurrentPosition(
    (geo) => {
      const city = geo.address.city
      const lat = geo.position.coords.latitude
      const lon = geo.position.coords.longitude
      const proxy = 'http://127.0.0.1:41417'
      const server =
        'https://api.openweathermap.org/data/2.5/onecall'
      const path = `?lat=${lat}&lon=${lon}&units=${UNITS}&lang=${LANG}&appid=${KEY}&`
      fetch(`${proxy}/${server}${path}`)
        .then((response) => {
          return response.json()
        })
        .catch((error) => {
          return dispatch({
            type: 'FETCH_FAILED',
            error: error,
          })
        })
        .then((data) => {
          return dispatch({
            type: 'FETCH_SUCCEDED',
            data: data,
            city: city,
          })
        })
    },
    (error) => {
      return dispatch({ type: 'GEO_FAILED', error: error })
    },
  )
}

export const updateState = (event, previousState) => {
  if (event.error) {
    return {
      ...previousState,
      warning: `We got an error: ${event.error}`,
    }
  }

  switch (event.type) {
    case 'FETCH_SUCCEDED':
      const { data, city, loading } = event
      return { ...previousState, loading, data, city }
    case 'SHOW_MORE':
    case 'HIDE_MORE':
      const { showMore } = event
      return { ...previousState, showMore }
    default:
      return previousState
  }
}

export const render = (
  { loading, data, city, showMore, error },
  dispatch,
) => {
  return error ? (
    <div className={errorWrapper}>
      Something went wrong: <strong>{String(error)}</strong>
    </div>
  ) : (
    <div>
      {loading || !data ? (
        <div className={errorWrapper}>Loading...</div>
      ) : (
        <div className={wrapper}>
          <div className={header}>
            <p className={info}>{city}</p>
            <p className={info}>
              {TEXT.feelsLike[LANG]}
              {data.current.feels_like.toFixed(1)}°
            </p>
            <div className={brief}>
              <div className={temperature}>
                {data.current.temp.toFixed(1)}°
              </div>
              {Array.isArray(data.current.weather) &&
                data.current.weather.map((deets) => (
                  <img
                    key={deets.id}
                    src={ICON[deets.icon] || undefined}
                    alt={deets.main}
                    title={deets.description}
                  />
                ))}
            </div>
          </div>

          <div className={hourly}>
            {Array.isArray(data.hourly) &&
              data.hourly.map((hourData, index) => {
                const date = new Date(hourData.dt * 1000)
                return index < 6 ? (
                  <div
                    key={hourData.dt}
                    className={everyHour}
                  >
                    <p className={everyHourTime}>
                      {new Intl.DateTimeFormat('en-US', {
                        dayPeriod: 'narrow',
                        hour: 'numeric',
                      }).format(date)}
                    </p>
                    <p className={everyHourTemp}>
                      {hourData.temp.toFixed(1)}
                    </p>
                    {Array.isArray(hourData.weather) &&
                      data.current.weather.map((deets) => (
                        <img
                          className={everyHourIcon}
                          key={deets.id}
                          src={
                            ICON[deets.icon] || undefined
                          }
                          alt={deets.main}
                          title={deets.description}
                        />
                      ))}
                    <p className={everyHourProb}>
                      {'rain' in hourData &&
                      !!hourData.rain &&
                      '1h' in hourData.rain
                        ? Math.min(
                            Number(
                              hourData.rain['1h'] *
                                CONFIDENCE_OF_RAINFALL *
                                100,
                            ).toFixed(2),
                            100,
                          )
                        : 0}
                      %
                    </p>
                  </div>
                ) : null
              })}
          </div>

          <div className={daily}>
            {Array.isArray(data.daily) &&
              data.daily.map((data, index) => {
                const date = new Date(data.dt * 1000)
                const weekDay = date.getDay()
                return index < 7 ? (
                  <div key={data.dt} className={everyDay}>
                    <div className={everyDayInfo}>
                      <div className={everyDayInfo}>
                        <p className={everyDayTime}>
                          {index === 0
                            ? TEXT.today[LANG]
                            : WEEK_DAY[LANG][weekDay]}
                        </p>
                        {Array.isArray(data.weather) &&
                          data.weather.map((deets) => (
                            <img
                              className={everyDayIcon}
                              key={deets.id}
                              src={
                                ICON[deets.icon] ||
                                undefined
                              }
                              alt={deets.main}
                              title={deets.description}
                            />
                          ))}
                      </div>
                      <div className={everyDayInfo}>
                        <p className={everyDayTempLow}>
                          {data.temp.min.toFixed(0)}
                        </p>
                        <p className={everyDayTempHigh}>
                          {data.temp.max.toFixed(0)}
                        </p>
                      </div>
                    </div>
                    <p className={everyDaySummary}>
                      {Array.isArray(data.weather) &&
                        data.weather.map((deets) => (
                          <span key={deets.id}>
                            {deets.description}
                          </span>
                        ))}
                    </p>
                  </div>
                ) : null
              })}
          </div>
        </div>
      )}
    </div>
  )
}

